`,(0,n.jsx)(e.div,{className:"rehype-code-title",children:"hooks/index.ts"}),(0,n.jsx)(e.pre,{className:"language-tsx",children:(0,n.jsxs)(e.code,{className:"language-tsx code-highlight",raw:`import { useTheme } from "next-themes";
import { useEffect, useState } from "react";
import { ITheme } from "../types";

export const useMultipleTheme = (themes: ITheme[]) => {
  const { theme, setTheme } = useTheme();
  const currentIndex = Math.abs(themes.findIndex((t) => t.name === theme));
  const currentTheme = themes[currentIndex];
  const nextTheme = themes[(currentIndex + 1) % themes.length];

  return {
    theme,
    setTheme,
    currentTheme,
    nextTheme,
  };
};

